import React from "react";
import "./LoaderLogo.css";

class LogoLoader extends React.Component {
  render() {
    const theme = this.props.theme;
    return (
      <div style={{ textAlign: "center" }}>
        <svg
          className="raw_logo"
          width="60%"  // un peu plus grand
          height="50%"
          viewBox="0 0 440 305"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          {/* Hexagone animé */}
          <path
            className="myHexagon"
            d="M293.545 167.405L229.5 204.381C227.025 205.81 223.975 205.81 221.5 204.381L157.455 167.405C154.98 165.976 153.455 163.335 153.455 160.476L153.455 86.5234C153.455 83.6653 154.98 81.0243 157.455 79.5952L221.5 42.6187C223.975 41.1896 227.025 41.1897 229.5 42.6187L293.545 79.5952C296.02 81.0243 297.545 83.6653 297.545 86.5234L297.545 160.476C297.545 163.335 296.02 165.976 293.545 167.405Z"
            stroke={theme.body}
            strokeWidth="4"
          />

          {/* Initiales animées */}
          <text
            className="letter"
            x="50%"
            y="50%"
            textAnchor="middle"
            dominantBaseline="middle"
            fontSize="120" // plus grand
            fill={theme.body}
            fontFamily="'Poppins', sans-serif" // belle police moderne
            fontWeight="700"
          >
            WD
          </text>

          <defs>
            <style
              dangerouslySetInnerHTML={{
                __html: `
                .myHexagon {
                  stroke-dasharray: 800;
                  stroke-dashoffset: 800;
                  animation: dash 4s linear forwards 0.5s;
                }

                .letter {
                  opacity: 0;
                  animation: fadein 2s linear forwards 2.5s;
                }

                @keyframes dash {
                  from { stroke-dashoffset: 800; }
                  to { stroke-dashoffset: 0; }
                }

                @keyframes fadein {
                  from { opacity: 0; }
                  to { opacity: 1; }
                }
                `,
              }}
            />
          </defs>
        </svg>

        {/* Nom complet sous le logo */}
        <div
          style={{
            color: theme.body,
            marginTop: "15px",
            fontSize: "32px", // plus gros
            fontFamily: "'Poppins', sans-serif",
            fontWeight: "600",
            textAlign: "center",
          }}
        >
          Wissam Draou
        </div>
      </div>
    );
  }
}

export default LogoLoader;
